import React, { useEffect, useState } from "react";
import { gapi } from "gapi-script";
import { useNavigate } from "react-router-dom";

const clientId = "901238362479-qhi62371a9f08ma2jmlmbh1vbctruivj.apps.googleusercontent.com";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    const initClient = () => {
      gapi.load("client:auth2", () => {
        gapi.client.init({
          clientId: clientId,
          scope: "https://www.googleapis.com/auth/classroom.courses.readonly",
        });
      });
    };
    initClient();
  }, []);

  const handleGoogleLogin = () => {
    const authInstance = gapi.auth2.getAuthInstance();
    authInstance.signIn().then((googleUser) => {
      const token = googleUser.getAuthResponse().access_token;
      console.log("Google ID Token:", token);

      fetch("http://localhost:5000/auth/google", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ token }),
      })
        .then((res) => res.json())
        .then((data) => {
          localStorage.setItem("token", token);
          navigate("/dashboard");
        })
        .catch((error) => console.error("Error:", error));
    });
  };

  const handleManualLogin = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/auth/manual", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.token) {
          localStorage.setItem("token", data.token);
          navigate("/dashboard");
        } else {
          alert("Invalid credentials");
        }
      })
      .catch((error) => console.error("Manual login error:", error));
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
      <h2>Login</h2>

      {/* Manual Login Form */}
      <form onSubmit={handleManualLogin}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ display: "block", width: "100%", marginBottom: "10px" }}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ display: "block", width: "100%", marginBottom: "10px" }}
          />
        </div>
        <button type="submit" style={{ width: "100%", marginBottom: "15px" }}>
          Login
        </button>
      </form>

      {/* Google Login */}
      <button onClick={handleGoogleLogin} style={{ width: "100%" }}>
        Sign in with Google
      </button>
    </div>
  );
};

export default Login;
